mapper.select:
SELECT notesno, name, cnt, rdate, seqno, visible 
FROM notes 
WHERE notesno=#{notesno}

mapper.update:
UPDATE notes 
SET name=#{name}, seqno=#{seqno} 
WHERE notesno=#{notesno}

UPDATE notes
SET seqno = seqno - 1
WHERE notesno = #{notesno}

UPDATE notes
SET seqno = seqno + 1
WHERE notesno = #{notesno}

UPDATE notes 
SET visible='Y'
WHERE notesno=#{notesno}

UPDATE notes 
SET visible='N'
WHERE notesno=#{notesno}

UPDATE notes 
SET cnt = cnt + 1 
WHERE notesno = #{notesno}

UPDATE notes 
SET cnt = cnt - 1 
WHERE notesno = #{notesno}
