mapper.update:
UPDATE restcontents 
SET map=#{map} 
WHERE restcontentsno=#{restcontentsno}

UPDATE restcontents 
SET youtube=#{youtube} 
WHERE restcontentsno=#{restcontentsno}

mapper.select:
SELECT COUNT(*) as cnt FROM restcontents
WHERE restcateno=#{restcateno}
WHERE restcateno=#{restcateno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT restcontentsno, adminno, restcateno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, r FROM ( SELECT restcontentsno, adminno, restcateno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, rownum as r FROM ( SELECT restcontentsno, adminno, restcateno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube FROM restcontents
WHERE restcateno=#{restcateno}
WHERE restcateno=#{restcateno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY restcontentsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM restcontents
WHERE restcontentsno = #{restcontentsno} AND passwd=#{passwd}

mapper.update:
UPDATE restcontents
SET title=#{title}, content=#{content},  word=#{word} 
WHERE restcontentsno = #{restcontentsno}

UPDATE restcontents
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE restcontentsno = #{restcontentsno}

mapper.select:
SELECT COUNT(*) as cnt 
FROM restcontents 
WHERE restcateno=#{restcateno}

mapper.update:
UPDATE restcontents
SET replycnt = replycnt + 1
WHERE restcontentsno = #{restcontentsno}

UPDATE restcontents
SET replycnt = replycnt - 1
WHERE restcontentsno = #{restcontentsno}
